import React, { Component } from "react";
import NewsItem from "./NewsItem";

export class News extends Component {

  constructor(){
    super();
    this.state={
      articles:[],
      loading:false,
      page:1
    }
  }
  async componentDidMount(){
    let url=`https://newsapi.org/v2/top-headlines?country=in&apiKey=26f6d023503d4880b4c7347f0b576c63&page=1&pageSize=${this.props.pageSize}`;
    let data=await fetch(url);
    let parsedData=await data.json()
    console.log(parsedData)
    this.setState({articles:parsedData.articles,totalResults:parsedData.totalResults})
  }

    handlePrevClick=async()=>{
        console.log("Previous")
        let url=`https://newsapi.org/v2/top-headlines?country=in&apiKey=26f6d023503d4880b4c7347f0b576c63&page=${this.state.page-1}&pageSize=${this.props.pageSize}`;
        let data=await fetch(url);
        let parsedData=await data.json()
        console.log(parsedData)
        // this.setState({articles:parsedData.articles})
            this.setState({
                page:this.state.page-1,
                articles:parsedData.articles            
            })
}

    handleNextClick=async()=>{
        console.log("Next")
        if(this.state.page+1>Math.ceil(this.state.totalResults)){

        }
        else{
                let url=`https://newsapi.org/v2/top-headlines?country=in&apiKey=26f6d023503d4880b4c7347f0b576c63&page=${this.state.page+1}&pageSize=${this.props.pageSize}`;
                let data=await fetch(url);
                let parsedData=await data.json()
                console.log(parsedData)
                // this.setState({articles:parsedData.articles})
            this.setState({
                page:this.state.page+1,
                articles:parsedData.articles            
        })
        }
    // let url=`https://newsapi.org/v2/top-headlines?country=in&apiKey=26f6d023503d4880b4c7347f0b576c63&page=${this.state.page+1}&pageSize=20`;
    // let data=await fetch(url);
    // let parsedData=await data.json()
    // console.log(parsedData)
    // // this.setState({articles:parsedData.articles})
    //     this.setState({
    //         page:this.state.page+1,
    //         articles:parsedData.articles            
    //     })
    
}

  render() {
    return (
      <div className="container">
        <header>NewsDozer - Top Headlines</header>
        <section className="section" >
        {this.state.articles.map((element)=>{
        return <article className="article"key={element.url} ><br /><br /><br /><br /><br />
            <NewsItem  title={element.title?element.title.slice(0,42):""} description={element.description?element.description.slice(0,100):""} imageUrl={element.urlToImage} newsUrl={element.url} />
            </article>
        })}
        </section>
            <div className="footer">
                {this.state.page<=1?(<span></span>):<button className="cta-1" disabled={this.state.page<=1} onClick={this.handlePrevClick}>
            <img src="https://www.svgrepo.com/download/410327/left-arrow.svg" alt="" />
            <span className="hover-underline-animation"> Previous </span>
        </button>}
                
                {/* <button className="cta-1" disabled={this.state.page<=1} onClick={this.handlePrevClick}>
                    <img src="https://www.svgrepo.com/download/410327/left-arrow.svg" alt="" />
                    <span className="hover-underline-animation"> Previous </span>
                </button> */}
                {this.state.page+1>Math.ceil(this.state.totalResults/this.props.pageSize)?(<span></span>):
                <button className="cta-2" onClick={this.handleNextClick}>
                <span className="hover-underline-animation"> Next </span>
                <svg viewBox="0 0 46 16" height="10" width="30" xmlns="http://www.w3.org/2000/svg" id="arrow-horizontal">
                    <path transform="translate(30)" d="M8,0,6.545,1.455l5.506,5.506H-30V9.039H12.052L6.545,14.545,8,16l8-8Z" data-name="Path 10" id="Path_10"></path>
                </svg>
            </button>}
                
                {/* <button className="cta-2" onClick={this.handleNextClick}>
                    <span className="hover-underline-animation"> Next </span>
                    <svg viewBox="0 0 46 16" height="10" width="30" xmlns="http://www.w3.org/2000/svg" id="arrow-horizontal">
                        <path transform="translate(30)" d="M8,0,6.545,1.455l5.506,5.506H-30V9.039H12.052L6.545,14.545,8,16l8-8Z" data-name="Path 10" id="Path_10"></path>
                    </svg>
                </button> */}
            </div>
      </div>
    );
  }
}

export default News;
