import React, { Component } from 'react'

export class About extends Component {
  render() {
    return (
      <div className='container-3'>
        <h1 className='about'>About us</h1>
        <p className='about'>Welcome to NewsDozer! We are your go-to source for the latest news and updates from around the world. Our website is designed to provide you with easy access to news articles on a variety of topics, including sports, entertainment, health, technology, science, and more.

          we bring you the most accurate and up-to-date information on the topics that matter most to you. We use the latest technologies, including ReactJS, to deliver a fast and seamless browsing experience that is both user-friendly and aesthetically pleasing.

          Whether you're a sports enthusiast, a science buff, or simply looking to stay informed on the latest happenings in the world, NewsDozer has you covered. So why wait? Browse our website today and stay informed on the topics that matter most to you!
        </p>
        <footer className='about'>&copy;copyright 2023</footer>
      </div>
    )
  }
}

export default About
